import React from 'react';
import { View, Text, StyleSheet } from 'react-native';

const PackageGiftCard = () => {
    return (
        <View style={styles.card_container}>
            <View style={styles.card_heading}>
                <Text style={styles.cardStyle}>Packages & Offers</Text>
                <Text style={styles.card_btnStyle}>Show All</Text>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    card_container: {
        width: '100vw',
        height: '40vh',
        backgroundColor: '#808080',
    },
    card_heading: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        padding: "10px",
        margin: '20px',
    },
    cardStyle: {
        color: "#102C56",
        fontSize: '19px',
        lineHeight: "14px",
        letterSpacing: '2%',
        fontWeight: 500,
    },
    card_btnStyle: {
        color: "#F98700",
        fontSize: '17px',
        lineHeight: "18px",
        letterSpacing: '2%',
        fontWeight: 500,
    },
})

export default PackageGiftCard;
