import React from 'react';
import { View, Text, Image, StyleSheet } from 'react-native';

const PackageGiftCard = ({ card, btn }) => {
    return (
        <View style={styles.card_container}>
            <View style={styles.card_heading}>
                <Text style={styles.cardStyle}>{card}</Text>
                <Text style={styles.card_btnStyle}>{btn}</Text>
            </View>
            <View style={styles.card_box}>
                <Image source={require("../../assets/hd.png")} style={styles.card_img} />
                <Image source={require("../../assets/hd.png")} style={styles.card_img} />
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    card_container: {
        width: '100vw',
        height: '40vh',
        backgroundColor: '#FBFCFE;',
    },
    card_heading: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        padding: "10px",
        margin: '20px',
    },
    cardStyle: {
        color: "#102C56",
        fontSize: '19px',
        lineHeight: "14px",
        letterSpacing: '2%',
        fontWeight: 500,
    },
    card_btnStyle: {
        color: "#F98700",
        fontSize: '17px',
        lineHeight: "18px",
        letterSpacing: '2%',
        fontWeight: 500,
    },
    // card_box
    card_box: {
        width: '100vw',
        height: "62px",
        backgroundColor: "#909090",
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'space-around',
        alignItems: 'center'
    },
    card_img: {
        width: '100px',
        height: '50px',
    }
})

export default PackageGiftCard;
