import React from 'react';
import { View, Image, Text, StyleSheet } from 'react-native';
import { homeData } from '../../utils/HomeData';

const Home = () => {
    return (
        <View style={styles.home_}>
            <View style={styles.home_box}>
                <View style={styles.home_heading}>
                    <Text style={styles.serviceStyle}>Services</Text>
                    <Text style={styles.btnStyle}>Show All</Text>
                </View>
                <View style={styles.box_container}>
                    {homeData.map((data) => (
                        <View style={styles.box_container_box}>
                            <Text style={{ backgroundColor: 'green', padding: '10px', borderRadius: '10px' }}>
                                <Image source={data.src} style={styles.home_box_img} />
                            </Text>
                            <Text>{data.name}</Text>
                        </View>
                    ))}
                </View>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    home_: {
        width: "100vw",
        height: '100vh',
        backgroundColor: "#fff",
        display: 'inline-block',
    },
    home_box: {
        width: '95vw',
        height: '210px',
        backgroundColor: "#fff",
        borderRadius: '5px',
        margin: 'auto',
    },
    home_heading: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        padding: "10px",
        margin: '10px',
    },
    serviceStyle: {
        color: "#102C56",
        fontSize: '19px',
        lineHeight: "14px",
        letterSpacing: '2%',
        fontWeight: 500,
    },
    btnStyle: {
        color: "#F98700",
        fontSize: '17px',
        lineHeight: "18px",
        letterSpacing: '2%',
        fontWeight: 500,
    },
    // box_container
    box_container: {
        width: '100%',
        flex: 1,
        flexDirection: 'row',
        backgroundColor: "#808080",
        justifyContent: 'space-between',
        padding: '10px'
    },
    box_container_box: {
        flex: 1,
        flexDirection: 'column',
        alignItems: 'center',
    },
    home_box_img: {
        width: '40px',
        height: '50px',
        opacity: 0.5,
        backgroundColor: "transparent",
        

    }

})

export default Home;
