import { Flex } from 'native-base';
import React from 'react';
import { View, Text, StyleSheet } from 'react-native';
import { PackageIcons } from './';

const Packages = () => {
    return (
        <View>
            <PackageIcons />
            <View style={styles.home_heading}>
                <Text style={styles.serviceStyle}>Packages & Offers</Text>
                <Text style={styles.btnStyle}>Show All</Text>
            </View>
            <View style={styles.package_flex}>
                <View style={styles.package_box}>
                    <Text style={styles.preStyles}>Premium Package</Text>
                    <Text>Hair Spa + Pedicure & Mainicure+ <br /> Massage + Hair Cut & more...</Text>
                </View>
                <View style={styles.package_box}>

                </View>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    home_heading: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        padding: "10px",
        margin: '10px',
    },
    serviceStyle: {
        color: "#102C56",
        fontSize: '19px',
        lineHeight: "14px",
        letterSpacing: '2%',
        fontWeight: 500,
    },
    btnStyle: {
        color: "#F98700",
        fontSize: '17px',
        lineHeight: "18px",
        letterSpacing: '2%',
        fontWeight: 500,
    },

    //box
    package_flex: {
        minWidth: '100px',
        padding: '10px',
        Flex: 1,
        flexDirection: 'row',
        backgroundColor: "orange",
    },
    package_box: {
        width: '200px',
        height: "300px",
        backgroundColor: "#fff",
        borderRadius: '10px',
        padding: '20px',
    },
    preStyles: {
        padding: '10px',
        color: "orange",
        fontSize: '15px',
        fontWeight: '600',
    }
})

export default Packages;
