import React from 'react';
import { View, Text, StyleSheet } from 'react-native';

const Header = () => {
    return (
        <View style={styles.h_container}>
            <View style={styles.header_box}>
                <Text>Hi Jackson</Text>
                <Text style={{ marginTop: '10px' }}>Hi Jackson</Text>
            </View>
            <View style={styles.header__Box}></View>
        </View>
    );
}

const styles = StyleSheet.create({
    h_container: {
        width: '100vw',
        minHeight: '20vh',
        backgroundColor: "blue",
        borderBottomLeftRadius: '10%'
    },
    header_box: {
        // padding: 40,
        flex: 1,
        flexDirection: 'row',
    },
    header__Box: {
        width: '80vw',
        height: '20vh',
        backgroundColor: "#fff",
        margin: "40px auto",
    }

})

export default Header;
