import React from 'react';
import { View, Text, StyleSheet, Image } from 'react-native';
import h1 from '../../images/h1.png'

const Header = () => {
    return (
        <View style={styles.h_container}>
            <View style={styles.header_box}>
                <Text>Hi Jackson</Text>
                <Text style={{ marginTop: '10px' }}>Hi Jackson</Text>
            </View>
            <View style={styles.header__Box}>
                <Text style={styles.boxText_1}>Upto 50%</Text>
                <View style={styles.textFlex}>
                    <Text style={styles.textStyle}>
                        <Text>Look more beautiful and <br />Save more discount </Text>
                        <Text>Get Offer Now </Text>
                    </Text>
                    <Text style={styles.img}>
                        <Image source={h1} />
                    </Text>
                </View>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    h_container: {
        width: '100vw',
        minHeight: '45vh',
        backgroundColor: "blue",
        borderBottomLeftRadius: '10%',
        display: 'inline-block',
    },
    header_box: {
        flex: 1,
        flexDirection: 'row',
        padding: '20px'
    },
    header__Box: {
        width: '80vw',
        minHeight: '20vh',
        backgroundColor: "#fff",
        margin: "auto",
        borderRadius: '10px',

    },
    boxText_1: {
        color: "orange",
        padding: '10px',
        fontWeight: '500'
    },
    textFlex: {
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'space-between',
        padding: '10px',
    },
    textStyle: {
        color: "#2C685A",
        padding: '10px',
        fontSize: '17px',
        fontWeight: '600',
    },
    img: {
        width: '80px',
        height: '80px',
        borderRadius: '50%',
        // backgroundColor: "orange",
    }
})

export default Header;
