import React from 'react';
import { View, Text, StyleSheet } from 'react-native';

const Header = () => {
    return (
        <View style={styles.h_container}>
            <View style={styles.header_box}>
                <Text>Hi Jackson</Text>
                <Text style={{ marginTop: '10px' }}>Hi Jackson</Text>
            </View>
            <View style={styles.header__Box}>
                <Text style={styles.boxText_1}>Upto 50%</Text>
                <View style={styles.textFlex}>
                    <Text style={styles.textStyle}>
                        <Text>Look more beautiful and <br />Save more discount </Text>
                        <Text>Get Offer Now </Text>
                    </Text>
                    <Text style={styles.img}>image</Text>
                </View>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    h_container: {
        // width: '100vw',
        // height: '35vh',
        // backgroundColor: "blue",
        // borderBottomLeftRadius: '10%',
        // display: 'inline-block',
        position: 'absolute',
        width: '375px',
        height: '302px',
        left: '0px',
        top: '0px',
        backgroundColor: "blue",
    },
    header_box: {
        // flex: 1,
        // flexDirection: 'row',
        // padding: '20px'
    },
    header__Box: {
        // width: '80vw',
        // height: '20vh',
        // backgroundColor: "#fff",
        // margin: "auto",
        // borderRadius: '10px',
        width: '330px',
        height: '156px',
        left: 0,
        right: 0,
        top: 0
    },
    boxText_1: {
        color: "orange",
        padding: '10px',
        fontWeight: '500'
    },
    textFlex: {
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'space-between',
        padding: '10px',
    },
    textStyle: {
        color: "#2C685A",
        padding: '10px',
        fontSize: '17px',
        fontWeight: '600',
    },
    img: {
        width: '167px',
        height: '111px',
        borderRadius: '50%',
        backgroundColor: "orange",
        position: 'absolute',
    }
})

export default Header;
