import React from 'react';
import { View, Text, StyleSheet, Image, } from 'react-native';
import MiddleHeader from './MiddleHeader';


// const hdimg = require("../../images/hd.png")
const Header = () => {
    return (
        <View style={styles.h_container}>
            <View style={styles.top_Heading}>
                <Text style={styles.textStyle}>Hii Jackson</Text>
                <View style={styles.textFlex}>
                    <Text style={styles.text_Styles}>Welcome  to the Salonnz!!!</Text>
                    <Text>Icon</Text>
                </View>
            </View>
            <MiddleHeader />
            <View style={styles.dot}>
                <Text style={{ width: '10px', height: '10px', borderRadius: '50%' }}>1</Text>
                <Text>2</Text>
                <Text>3</Text>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    h_container: {
        width: '100vw',
        minHeight: '62vh',
        backgroundColor: "blue",
        borderBottomLeftRadius: '10%',
    },
    top_Heading: {
        padding: '20px',
        margin: '10px',
    },
    textStyle: {
        fontSize: '16px',
        color: "#fff",
        lineHeight: '21px',
        letterSpacing: '1%',
    },
    textFlex: {
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'space-between',
    },
    text_Styles: {
        fontSize: '20px',
        color: "#fff",
        lineHeight: '26px',
        letterSpacing: '1%',
        marginTop: '10px',
    },
    dot: {
        flex: 1,
        flexDirection: 'row',
        alignItems: 'center',
        margin: 'auto'
    }
})

export default Header;


