import React from 'react';
import { View, Text, StyleSheet, Image, } from 'react-native';


// const hdimg = require("../../images/hd.png")
const Header = () => {
    return (
        <View style={styles.h_container}>
            <View style={styles.header_box}>
                <Text>Hi Jackson</Text>
                <Text style={{ marginTop: '10px' }}>Hi Jackson</Text>
            </View>

           
        </View>
    );
}

const styles = StyleSheet.create({
    h_container: {
        width: '100vw',
        minHeight: '45vh',
        backgroundColor: "blue",
        borderBottomLeftRadius: '10%',
        display: 'inline-block',
    },
    header_box: {
        flex: 1,
        flexDirection: 'row',
        padding: '20px'
    },
    header__Box: {
        width: '95vw',
        minHeight: '220px',
        backgroundColor: "#fff",
        margin: "auto",
        borderRadius: '10px',

    },
    boxText_1: {
        color: "orange",
        padding: '10px',
        fontWeight: '500',
        marginTop: '10px',
    },
    textFlex: {
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'space-between',
        padding: '10px',
        position: 'relative',
    },
    textStyle: {
        color: "#2C685A",
        padding: '10px',
        fontSize: '17px',
        fontWeight: '600',
    },
    imgs: {
        width: '120px',
        height: '120px',
        borderRadius: '50%',
        backgroundColor: "orange",
        position: 'relative',
        padding: '10px',
    },
    imgss: {
        width: '120px',
        height: '120px',
        marginTop: '20px',
        top: 20,
        left: 1,
        borderBottomLeftRadius: '4px',
        position: "absolute",
    },
    offerStyle: {
        width: '70%',
        marginTop: '20px',
        lineHeight: '12px',
        color: "orange",
        fontWeight: '600',
        border: '1px solid #808080',
        padding: '6px',
        borderRadius: '10px',
    }
})

export default Header;
