import React from 'react';
import { View, Text, StyleSheet } from 'react-native';

const Header = () => {
    return (
        <View style={styles.h_container}>
            <View style={styles.header_box}>
                <Text>Hi Jackson</Text>
                <Text style={{ marginTop: '10px' }}>Hi Jackson</Text>
            </View>
            <View style={styles.header__Box}>
                <Text style={styles.boxText_1}>Upto 50%</Text>
                <View style={styles.textFlex}>
                    <Text style={{flex:'1'}}>
                        <Text>Look more beautiful and </Text>
                        <Text>Save more discount </Text>
                    </Text>
                    <Text>image</Text>
                </View>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    h_container: {
        width: '100vw',
        height: '35vh',
        backgroundColor: "blue",
        borderBottomLeftRadius: '10%',
        display: 'inline-block'
    },
    header_box: {
        flex: 1,
        flexDirection: 'row',
        padding: '20px'
    },
    header__Box: {
        width: '80vw',
        height: '20vh',
        backgroundColor: "#fff",
        margin: "auto",
        borderRadius: '10px',
    },
    boxText_1: {
        color: "orange",
        padding: '10px',
        fontWeight: '500'
    },
    textFlex: {
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'space-between'
    }
})

export default Header;
