import React from 'react';
import { View, Text, StyleSheet, Image, Pressable } from 'react-native';
import { Button } from 'react-native-paper';
import { SafeAreaView } from 'react-native-safe-area-context';


// const hdimg = require("../../images/hd.png")
const Header = () => {
    return (
        <SafeAreaView>
            <View style={styles.h_container}>
                <View style={styles.header_box}>
                    <Text>Hi Jackson</Text>
                    <Text style={{ marginTop: '10px' }}>Hi Jackson</Text>
                </View>

                <View style={styles.header__Box}>
                    <Text style={styles.boxText_1}>Upto 50%</Text>
                    <View style={styles.textFlex}>
                        <Text style={styles.textStyle}>
                            <Text>Look more beautiful and <br />Save more discount </Text>
                            <br />
                            <Pressable style={styles.button} onPress={onPress}>
                                <Text style={styles.text}>{title}</Text>
                            </Pressable>
                        </Text>
                        <Text style={styles.imgs}>
                            <Image style={styles.imgss} source={require("../../assets/hd.png")} />
                        </Text>
                    </View>
                </View>
            </View>
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    h_container: {
        width: '100vw',
        minHeight: '45vh',
        backgroundColor: "blue",
        borderBottomLeftRadius: '10%',
        display: 'inline-block',
    },
    header_box: {
        flex: 1,
        flexDirection: 'row',
        padding: '20px'
    },
    header__Box: {
        width: '95vw',
        minHeight: '210px',
        backgroundColor: "#fff",
        margin: "auto",
        borderRadius: '10px',

    },
    boxText_1: {
        color: "orange",
        padding: '10px',
        fontWeight: '500'
    },
    textFlex: {
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'space-between',
        padding: '10px',
        position: 'relative'
    },
    textStyle: {
        color: "#2C685A",
        padding: '10px',
        fontSize: '17px',
        fontWeight: '600',
    },
    imgs: {
        width: '120px',
        height: '120px',
        borderRadius: '50%',
        backgroundColor: "orange",
        position: 'relative',
        padding: '10px',
    },
    imgss: {
        width: '120px',
        height: '120px',
        marginTop: '20px',
        top: 20,
        left: 1,
        borderBottomLeftRadius: '4px',
        position: "absolute",
    },
    button: {
        alignItems: 'center',
        justifyContent: 'center',
        paddingVertical: 12,
        paddingHorizontal: 32,
        borderRadius: 4,
        elevation: 3,
        backgroundColor: 'black',
    },
})

export default Header;
